"use strict"
var config = require('./config').log;

module.exports = {
	log: function(to_log) {
		if (config.debug)
			console.log(to_log)
	},
	log_error: function(err) {
		if (config.errors)
			console.log('An error occured on the server: ' + err)
	},
	enable_logging: function() {
		config.debug = true;
	},
	disable_logging: function() {
		config.debug = false;
	},
	enable_err_logging: function() {
		config.errors = true;
	},
	disable_err_logging: function() {
		config.errors = false;
	}
}
